using TMPro;
using UnityEngine;

public class TextFromData : MonoBehaviour
{
    public bool UseFixed = false;
    public string type = "";
    private TextMeshProUGUI jessie;
    public int walter;
    public Card FuckMyShit;
    // Start is called before the first frame update
    void OnEnable()
    {
        jessie = GetComponent<TextMeshProUGUI>();
    }

    // Update is called once per frame
    void Update()
    {
        if (!UseFixed)
        {
            j();
        }
    }
    void FixedUpdate()
    {
        if (UseFixed)
        {
            j();
        }
    }

    public void j()
    {
        string a = "";
        switch (type)
        {
            case "TestNumber":
                a = RandomFunctions.Instance.NumToRead("");
                break;
            case "TestNumber2":
                a = RandomFunctions.Instance.NumToRead("-.169");
                break;
            case "Code":
                a = RelayMoment.Instance.Join_Code;
                break;
            case "Health":
                a = (int)Gamer.Instance.Health + "/" + (int)Gamer.Instance.MaxHealth;
                break;
            case "Health2":
                a = (int)Gamer.Instance.Enemyhealth + "/" + (int)Gamer.Instance.MaxEnemyhealth;
                break;
            case "DeckSel":
                a = Gamer.Instance.LoadedDecks[Gamer.Instance.SelectedDeck][0];
                break;
            case "DeckSel2":
                a = Gamer.Instance.LoadedDecks[Gamer.Instance.SelectedDeckDos][0];
                break;
            case "Round":
                a = Gamer.Instance.Turncount.ToString();
                break;
            case "FPS":
                a = $"FPS: {(int)(1.0f / Time.smoothDeltaTime)}";
                break;
            case "AIDs":
                switch (Gamer.Instance.AiDiffSelect)
                {
                    case 0:
                        a = "Easy";
                        break;
                    case 1:
                        a = "Medium";
                        break;
                    case 2:
                        a = "Hard";
                        break;
                    case 3:
                        a = "Impossible";
                        break;
                }
                break;
            case "escape":
                a = $"Press \"{InputManager.keynames[InputManager.gamekeys["close_menu"]]}\" to exit";
                break;
            case "handss":
                a = $"Hand Size: {Gamer.Instance.HandSizeSel}";
                break;
            case "en1":
                a = Gamer.Instance.PLayerEnergy.ToString();
                break;
            case "en2":
                a = Gamer.Instance.AIEnergy.ToString();
                break;
            case "Players":
                int connects = Gamer.Instance.miscrefs[46].transform.childCount;
                a = $"Players ({connects}/2)";
                break;
            case "PlayerName":
                try
                {
                    var esex = Gamer.Instance.miscrefs[46].GetComponentsInChildren<PLayerHandywandy>();
                    a = esex[walter].Username;
                }
                catch
                {

                }
                break;
            case "ProfData":
                string e = "";
                foreach (var pp in SaveSystem.Instance.ProfileData)
                {
                    switch (pp.Key)
                    {
                        case "Title":
                            e += $"{pp.Key}: {Gamer.Instance.Titles[Gamer.Instance.SelectedTitle]}<br>";
                            break;
                        case "Time Played":
                            e += $"{pp.Key}: {RandomFunctions.Instance.TimeToRead(pp.Value)}<br>";
                            break;
                        case "Game Time Played":
                            e += $"{pp.Key}: {RandomFunctions.Instance.TimeToRead(pp.Value)}<br>";
                            break;
                        default:
                            e += $"{pp.Key}: {pp.Value}<br>";
                            break;
                    }
                }
                a = e;
                break;
            case "PTitle":
                a = $"\"{Gamer.Instance.Titles[Gamer.Instance.SelectedTitle]}\"";
                break;
            case "Readyers":
                int amnt = Gamer.Instance.AllHandJobs.Count;
                int amnt2 = 0;
                foreach (var hj in Gamer.Instance.AllHandJobs)
                {
                    if(hj.IsReady.Value || hj.IsOwnedByServer)
                    {
                        amnt2++;
                    }
                }


                a = $"{amnt2}/{amnt} Ready";
                break;
        }

        jessie.text = a;
    }
}
